import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int x0 = scanner.nextInt();
        int y0 = scanner.nextInt();
        int x1 = scanner.nextInt();
        int y1 = scanner.nextInt();

        for (int i = 0; i < scanner.nextInt(); i++) {
            int x2 = scanner.nextInt();
            int y2 = scanner.nextInt();

            int crossProduct = (x1 - x0) * (y2 - y0) - (x2 - x0) * (y1 - y0);

            if (crossProduct > 0) {
                System.out.println("COUNTER_CLOCKWISE");
            } else if (crossProduct < 0) {
                System.out.println("CLOCKWISE");
            } else {
                if ((x2 - x0) * (x2 - x0) + (y2 - y0) * (y2 - y0) < 1e-9 &&
                        (x2 - x1) * (x2 - x1) + (y2 - y1) * (y2 - y1) < 1e-9) {
                    System.out.println("ON_SEGMENT");
                } else if ((x2 - x0) * (y1 - y0) - (y2 - y0) * (x1 - x0) < 1e-9) {
                    System.out.println("ONLINE_BACK");
                } else {
                    System.out.println("ONLINE_FRONT");
                }
            }
        }
    }
}
