#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int x, y;
} Point;

int dot_product(Point p1, Point p2) {
    return p1.x * p2.x + p1.y * p2.y;
}

int main() {
    int q;
    scanf("%d", &q);

    for (int i = 0; i < q; i++) {
        Point p0, p1, p2, p3;
        scanf("%d %d %d %d %d %d %d %d", &p0.x, &p0.y, &p1.x, &p1.y, &p2.x, &p2.y, &p3.x, &p3.y);

        Point v1 = {p1.x - p0.x, p1.y - p0.y};
        Point v2 = {p3.x - p2.x, p3.y - p2.y};

        if (dot_product(v1, v2) == 0 && v1.x != 0 && v2.x != 0) {
            printf("2\n");
        } else if (dot_product(v1, v2) == 0) {
            printf("0\n");
        } else if (dot_product(v1, v2) == dot_product(v1, {v2.y, -v2.x}) || dot_product(v1, v2) == dot_product({v1.y, -v1.x}, v2)) {
            printf("1\n");
        } else {
            printf("0\n");
        }
    }

    return 0;
}
