#include <stdio.h>
#include <stdlib.h>

typedef struct {
    double x, y;
} Point;

typedef struct {
    Point p0, p1;
} Segment;

double cross(const Segment *s1, const Segment *s2) {
    return (s1->p1.x - s1->p0.x) * (s2->p2.y - s2->p3.y) - (s1->p1.y - s1->p0.y) * (s2->p2.x - s2->p3.x);
}

double dot(const Point *p0, const Point *p1) {
    return p0->x * p1->x + p0->y * p1->y;
}

double det(const Point *p0, const Point *p1) {
    return p0->x * p1->y - p0->y * p1->x;
}

Point cross_point(const Segment *s1, const Segment *s2) {
    double d = cross(s1, s2);
    if (d == 0) {
        printf("No cross point\n");
        exit(1);
    }

    double dx = cross(s1, (Segment *)s2);
    double dy = cross((Segment *)s2, s1);

    double x = s1->p0.x + dx / d * (s1->p1.x - s1->p0.x);
    double y = s1->p0.y + dy / d * (s1->p1.y - s1->p0.y);

    return (Point) {x, y};
}

int main() {
    int q;
    scanf("%d", &q);

    for (int i = 0; i < q; i++) {
        Segment s1, s2;
        scanf("%lf %lf %lf %lf %lf %lf %lf %lf",
              &s1.p0.x, &s1.p0.y, &s1.p1.x, &s1.p1.y,
              &s2.p0.x, &s2.p0.y, &s2.p1.x, &s2.p1.y);

        Point p = cross_point(&s1, &s2);
        printf("%.10lf %.10lf\n", p.x, p.y);
    }

    return 0;
}
