#include <iostream>
#include <iomanip>

struct Point {
    double x, y;
};

double cross(const Point& p0, const Point& p1, const Point& p2, const Point& p3) {
    double a = (p1.x - p0.x) * (p3.y - p2.y) - (p1.y - p0.y) * (p3.x - p2.x);
    double b = (p1.x - p0.x) * (p2.y - p1.y) - (p1.y - p0.y) * (p2.x - p1.x);
    double c = (p1.x - p0.x) * (p1.y - p0.y) - (p1.y - p0.y) * (p1.x - p0.x);

    if (a == 0) {
        return -1;
    }

    double t = b / a;
    double u = c / a;

    if (t < 0 || t > 1 || u < 0 || u > 1) {
        return -1;
    }

    return t;
}

int main() {
    int q;
    std::cin >> q;

    while (q--) {
        Point p0, p1, p2, p3;
        std::cin >> p0.x >> p0.y >> p1.x >> p1.y >> p2.x >> p2.y >> p3.x >> p3.y;

        double t = cross(p0, p1, p2, p3);

        if (t == -1) {
            std::cout << "No cross point" << std::endl;
        } else {
            double x = p0.x + t * (p1.x - p0.x);
            double y = p0.y + t * (p1.y - p0.y);

            std::cout << std::fixed << std::setprecision(20) << x << " " << y << std::endl;
        }
    }

    return 0;
}
