import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] x = new int[n];
        int[] y = new int[n];
        for (int i = 0; i < n; i++) {
            x[i] = scanner.nextInt();
            y[i] = scanner.nextInt();
        }
        boolean isConvex = isConvexPolygon(x, y, n);
        System.out.println(isConvex ? 1 : 0);
    }

    public static boolean isConvexPolygon(int[] x, int[] y, int n) {
        for (int i = 0; i < n; i++) {
            int j = (i + 1) % n;
            int k = (i + 2) % n;
            if (orientation(x[i], y[i], x[j], y[j], x[k], y[k]) > 0) {
                return false;
            }
        }
        return true;
    }

    public static int orientation(int x1, int y1, int x2, int y2, int x3, int y3) {
        int val = (x2 - x1) * (y3 - y1) - (x3 - x1) * (y2 - y1);
        if (val == 0) {
            return 0;
        } else if (val > 0) {
            return 1;
        } else {
            return -1;
        }
    }
}
