#include <stdio.h>
#include <stdlib.h>

typedef struct Point {
    int x, y;
} Point;

int orientation(Point p, Point q, Point r) {
    int val = (q.y - p.y) * (r.x - q.x) - (q.x - p.x) * (r.y - q.y);
    if (val == 0) return 0;
    return (val > 0) ? 1 : 2;
}

int isPointInside(Point p, Point *q, int n) {
    int i, cnt = 0;
    for (i = 0; i < n; i++) {
        if (orientation(p, q[i], q[(i + 1) % n]) == 0) return 1;
        if (orientation(p, q[i], q[(i + 1) % n]) == 2) cnt++;
    }
    return (cnt % 2 == 0) ? 0 : 1;
}

int main() {
    int n, q, i, j;
    Point p[100], q[100];

    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        scanf("%d %d", &p[i].x, &p[i].y);
    }
    scanf("%d", &q);
    for (i = 0; i < q; i++) {
        scanf("%d %d", &q[i].x, &q[i].y);
        printf("%d\n", isPointInside(q[i], p, n));
    }

    return 0;
}
