import java.util.Scanner;

public class Main {
    static class Point {
        int x, y;

        public Point(int x, int y) {
            this.x = x;
            this.y = y;
        }
    }

    static boolean onSegment(Point p, Point q, Point r) {
        return (q.x <= Math.max(p.x, r.x) && q.x >= Math.min(p.x, r.x) && q.y <= Math.max(p.y, r.y) && q.y >= Math.min(p.y, r.y));
    }

    static int orientation(Point p, Point q, Point r) {
        int val = (q.y - p.y) * (r.x - q.x) - (q.x - p.x) * (r.y - q.y);
        if (val == 0) return 0;
        return (val > 0) ? 1 : 2;
    }

    static boolean doIntersect(Point p1, Point q1, Point p2, Point q2) {
        int o1 = orientation(p1, q1, p2);
        int o2 = orientation(p1, q1, q2);
        int o3 = orientation(p2, q2, p1);
        int o4 = orientation(p2, q2, q1);

        return ((o1 != o2) && (o3 != o4));
    }

    static boolean isPointInside(Point p, Point[] points, int n) {
        int i = 0, j = n - 1;
        while (i < n) {
            if (doIntersect(points[i], points[j], p, p)) {
                if (onSegment(points[i], p, points[j])) return true;
                return false;
            }
            if (points[i].y < p.y) i++;
            else j--;
        }
        return false;
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        Point[] points = new Point[n];
        for (int i = 0; i < n; i++) {
            points[i] = new Point(scanner.nextInt(), scanner.nextInt());
        }
        int q = scanner.nextInt();
        while (q-- > 0) {
            int x = scanner.nextInt();
            int y = scanner.nextInt();
            Point p = new Point(x, y);
            if (isPointInside(p, points, n)) {
                if (isPointOnSegment(p, points, n)) System.out.println("1");
                else System.out.println("2");
            } else System.out.println("0");
        }
    }

    static boolean isPointOnSegment(Point p, Point[] points, int n) {
        for (int i = 0; i < n; i++) {
            if (points[i].x == p.x && points[i].y == p.y) return true;
        }
        return false;
    }
}
