def orientation(p, q, r):
    val = (q[1] - p[1]) * (r[0] - q[0]) - (q[0] - p[0]) * (r[1] - q[1])
    if val == 0:
        return 0
    return 1 if val > 0 else 2

def is_point_inside(polygon, point):
    n = len(polygon)
    inside = False
    i = 0
    while i < n:
        j = (i + 1) % n
        if (polygon[i][1] > point[1]) != (polygon[j][1] > point[1]) and (point[0] < (polygon[j][0] - polygon[i][0]) * (point[1] - polygon[i][1]) / (polygon[j][1] - polygon[i][1]) + polygon[i][0]):
            inside = not inside
        i = j
    return inside

def is_point_on_segment(polygon, point):
    n = len(polygon)
    for i in range(n):
        if (polygon[i][0] == point[0] and polygon[i][1] == point[1]):
            return True
        if (polygon[i][1] <= point[1] and point[1] < polygon[(i+1)%n][1] and
            (point[0] - polygon[i][0]) * (polygon[(i+1)%n][1] - polygon[i][1]) /
            (polygon[(i+1)%n][0] - polygon[i][0]) == point[1] - polygon[i][1]):
            return True
    return False

def main():
    n = int(input())
    polygon = []
    for _ in range(n):
        x, y = map(int, input().split())
        polygon.append((x, y))
    q = int(input())
    for _ in range(q):
        x, y = map(int, input().split())
        point = (x, y)
        if is_point_inside(polygon, point):
            print(2)
        elif is_point_on_segment(polygon, point):
            print(1)
        else:
            print(0)

if __name__ == "__main__":
    main()
