#include <stdio.h>

int main() {
    int n, i, j, min, max, count = 0;
    int minRow[100], maxCol[100];

    while (scanf("%d", &n) != EOF) {
        if (n == 0) break;

        for (i = 0; i < n; i++) {
            min = 1000;
            max = 0;
            for (j = 0; j < n; j++) {
                scanf("%d", &minRow[i]);
                if (minRow[i] < min) min = minRow[i];
                if (minRow[i] > max) max = minRow[i];
            }
            maxCol[i] = max;
        }

        for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
                if (minRow[i] == maxCol[j]) {
                    printf("%d\n", minRow[i]);
                    count++;
                    break;
                }
            }
            if (count) break;
        }

        if (!count) printf("0\n");

        for (i = 0; i < n; i++) {
            for (j = 0; j < n; j++) {
                scanf("%*d");
            }
        }
    }

    return 0;
}
