#include <iostream>
#include <cmath>
#include <limits>

struct Point {
    double x, y;
};

double distance(const Point& p1, const Point& p2) {
    return std::sqrt(std::pow(p1.x - p2.x, 2) + std::pow(p1.y - p2.y, 2));
}

double diameter(const Point points[], int n) {
    double maxDistance = 0;
    for (int i = 0; i < n; ++i) {
        for (int j = i + 1; j < n; ++j) {
            double dist = distance(points[i], points[j]);
            if (dist > maxDistance) {
                maxDistance = dist;
            }
        }
    }
    return maxDistance;
}

int main() {
    int n;
    std::cin >> n;
    Point points[n];
    for (int i = 0; i < n; ++i) {
        std::cin >> points[i].x >> points[i].y;
    }
    std::cout << std::fixed << std::setprecision(6) << diameter(points, n) << std::endl;
    return 0;
}
