#include <stdio.h>
#include <stdlib.h>
#include <math.h>

typedef struct Point {
    double x, y;
} Point;

double crossProduct(Point p1, Point p2) {
    return p1.x * p2.y - p1.y * p2.x;
}

double areaOfPolygon(Point *points, int n) {
    double area = 0;
    for (int i = 0; i < n; i++) {
        int j = (i + 1) % n;
        area += crossProduct(points[i], points[j]);
    }
    return abs(area) / 2;
}

double areaOfCutPolygon(Point *points, int n, Point p1, Point p2) {
    double area = 0;
    int left = 0;
    int right = n - 1;
    while (left <= right) {
        int mid = (left + right) / 2;
        double cross = crossProduct(points[mid], p2) - crossProduct(points[mid], p1);
        if (cross < 0) {
            right = mid - 1;
        } else if (cross > 0) {
            left = mid + 1;
        } else {
            left = mid;
            break;
        }
    }
    for (int i = left; i < n; i++) {
        int j = (i + 1) % n;
        area += crossProduct(points[i], points[j]);
    }
    for (int i = 0; i < left; i++) {
        int j = (i + 1) % n;
        area += crossProduct(points[i], points[j]);
    }
    return abs(area) / 2;
}

int main() {
    int n;
    scanf("%d", &n);
    Point *points = (Point *)malloc(n * sizeof(Point));
    for (int i = 0; i < n; i++) {
        scanf("%lf %lf", &points[i].x, &points[i].y);
    }
    int q;
    scanf("%d", &q);
    while (q--) {
        int p1x, p1y, p2x, p2y;
        scanf("%d %d %d %d", &p1x, &p1y, &p2x, &p2y);
        Point p1 = {p1x, p1y};
        Point p2 = {p2x, p2y};
        printf("%.10lf\n", areaOfCutPolygon(points, n, p1, p2));
    }
    free(points);
    return 0;
}
