import java.util.*;
import java.io.*;

public class Main {
    static class Point {
        double x, y;

        public Point(double x, double y) {
            this.x = x;
            this.y = y;
        }
    }

    static class Polygon {
        int n;
        Point[] points;

        public Polygon(int n, Point[] points) {
            this.n = n;
            this.points = points;
        }
    }

    static class Line {
        Point p1, p2;

        public Line(Point p1, Point p2) {
            this.p1 = p1;
            this.p2 = p2;
        }
    }

    static double crossProduct(Point p1, Point p2, Point p3) {
        return (p2.x - p1.x) * (p3.y - p1.y) - (p2.y - p1.y) * (p3.x - p1.x);
    }

    static double distance(Point p1, Point p2) {
        return Math.sqrt(Math.pow(p2.x - p1.x, 2) + Math.pow(p2.y - p1.y, 2));
    }

    static double area(Polygon polygon, Line line) {
        double area = 0;
        for (int i = 0; i < polygon.n; i++) {
            int j = (i + 1) % polygon.n;
            area += crossProduct(polygon.points[i], polygon.points[j], line.p1);
            area += crossProduct(polygon.points[i], polygon.points[j], line.p2);
        }
        return Math.abs(area / 2);
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int n = Integer.parseInt(br.readLine());
        Point[] points = new Point[n];
        StringTokenizer st = new StringTokenizer(br.readLine());
        for (int i = 0; i < n; i++) {
            points[i] = new Point(Double.parseDouble(st.nextToken()), Double.parseDouble(st.nextToken()));
        }
        int q = Integer.parseInt(br.readLine());
        Line[] lines = new Line[q];
        for (int i = 0; i < q; i++) {
            st = new StringTokenizer(br.readLine());
            lines[i] = new Line(new Point(Double.parseDouble(st.nextToken()), Double.parseDouble(st.nextToken())),
                    new Point(Double.parseDouble(st.nextToken()), Double.parseDouble(st.nextToken())));
        }
        Polygon polygon = new Polygon(n, points);
        for (int i = 0; i < q; i++) {
            System.out.printf("%.6f%n", area(polygon, lines[i]));
        }
    }
}
