import math

def cross_product(p1, p2, p3):
    return (p2[0] - p1[0]) * (p3[1] - p1[1]) - (p2[1] - p1[1]) * (p3[0] - p1[0])

def orientation(p1, p2, p3):
    return cross_product(p1, p2, p3)

def convex_cut(g, q, queries):
    n = int(g[0])
    points = [list(map(int, g[i].split())) for i in range(1, n + 1)]
    for query in queries:
        p1, p2 = list(map(int, query.split()))
        p1 = [p1, 0]
        p2 = [p2, 0]
        left = 0
        right = n - 1
        while left <= right:
            mid = (left + right) // 2
            if orientation(points[mid], points[0], p1) <= 0:
                right = mid - 1
            else:
                left = mid + 1
        left_area = 0
        for i in range(n):
            if i == left:
                continue
            if orientation(points[i], points[left], p1) <= 0:
                left_area += 0.5 * abs(points[i][0] * points[left][1] - points[left][0] * points[i][1])
        left = 0
        right = n - 1
        while left <= right:
            mid = (left + right) // 2
            if orientation(points[mid], points[0], p2) <= 0:
                left = mid + 1
            else:
                right = mid - 1
        right_area = 0
        for i in range(n):
            if i == right:
                continue
            if orientation(points[i], points[right], p2) <= 0:
                right_area += 0.5 * abs(points[i][0] * points[right][1] - points[right][0] * points[i][1])
        print(round(left_area + right_area, 7))

g = input()
