#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    int n;
    while (std::cin >> n && n != 0) {
        std::vector<std::vector<int>> matrix(n, std::vector<int>(n));
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < n; ++j) {
                std::cin >> matrix[i][j];
            }
        }

        int minRow = INT_MAX, maxCol = INT_MIN;
        for (int i = 0; i < n; ++i) {
            minRow = std::min(minRow, matrix[i][0]);
            maxCol = std::max(maxCol, matrix[0][i]);
        }

        bool found = false;
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < n; ++j) {
                if (matrix[i][j] == minRow && matrix[i][j] == maxCol) {
                    std::cout << matrix[i][j] << std::endl;
                    found = true;
                    break;
                }
            }
            if (found) break;
        }

        if (!found) {
            std::cout << 0 << std::endl;
        }
    }

    return 0;
}
