#include <iostream>
#include <vector>
#include <algorithm>

struct Segment {
    int x1, y1, x2, y2;
};

bool compareSegments(const Segment& a, const Segment& b) {
    if (a.x1 == b.x1) {
        return a.y1 < b.y1;
    }
    return a.x1 < b.x1;
}

int main() {
    int n;
    std::cin >> n;

    std::vector<Segment> segments(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> segments[i].x1 >> segments[i].y1 >> segments[i].x2 >> segments[i].y2;
    }

    std::sort(segments.begin(), segments.end(), compareSegments);

    int intersections = 0;
    int x = segments[0].x1;
    int y = segments[0].y1;
    int prevX = segments[0].x1;
    int prevY = segments[0].y1;

    for (int i = 1; i < n; ++i) {
        if (segments[i].x1 == x && segments[i].y1 == y) {
            continue;
        }
        if (segments[i].x1 == prevX && segments[i].y1 == prevY) {
            continue;
        }
        if (segments[i].x1 == x && segments[i].y1 > y) {
            ++intersections;
        }
        if (segments[i].x1 == x && segments[i].y1 < y) {
            ++intersections;
        }
        if (segments[i].x1 > x && segments[i].y1 == y) {
            ++intersections;
        }
        if (segments[i].x1 < x && segments[i].y1 == y) {
            ++intersections;
        }
        x = segments[i].x1;
        y = segments[i].y1;
        prevX = segments[i].x2;
        prevY = segments[i].y2;
    }

    std::cout << intersections << std::endl;

    return 0;
}
