import java.util.*;
import java.io.*;

public class Main {
    static class Segment {
        int x1, y1, x2, y2;
        int type;

        public Segment(int x1, int y1, int x2, int y2) {
            this.x1 = Math.min(x1, x2);
            this.y1 = y1;
            this.x2 = Math.max(x1, x2);
            this.y2 = y2;
            this.type = (x1 == x2) ? 0 : 1;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int n = Integer.parseInt(br.readLine());
        List<Segment> segments = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            String[] line = br.readLine().split(" ");
            segments.add(new Segment(Integer.parseInt(line[0]), Integer.parseInt(line[1]), Integer.parseInt(line[2]), Integer.parseInt(line[3])));
        }
        Collections.sort(segments, Comparator.comparingInt(s -> s.y1));
        Map<Integer, List<Integer>> map = new HashMap<>();
        for (int i = 0; i < n; i++) {
            if (!map.containsKey(segments.get(i).y1)) {
                map.put(segments.get(i).y1, new ArrayList<>());
            }
            map.get(segments.get(i).y1).add(segments.get(i).x1);
            if (segments.get(i).y2 != segments.get(i).y1) {
                if (!map.containsKey(segments.get(i).y2)) {
                    map.put(segments.get(i).y2, new ArrayList<>());
                }
                map.get(segments.get(i).y2).add(segments.get(i).x2);
            }
        }
        int count = 0;
        for (Map.Entry<Integer, List<Integer>> entry : map.entrySet()) {
            Collections.sort(entry.getValue());
            int i = 0;
            for (int j = 1; j < entry.getValue().size(); j++) {
                if (entry.getValue().get(j) > entry.getValue().get(i)) {
                    count++;
                    i = j;
                }
            }
        }
        System.out.println(count);
    }
}
