import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int c1x = scanner.nextInt();
        int c1y = scanner.nextInt();
        int c1r = scanner.nextInt();
        int c2x = scanner.nextInt();
        int c2y = scanner.nextInt();
        int c2r = scanner.nextInt();

        int distance = (int) Math.sqrt(Math.pow(c2x - c1x, 2) + Math.pow(c2y - c1y, 2));

        if (distance > c1r + c2r) {
            System.out.println("4");
        } else if (distance == c1r + c2r) {
            System.out.println("0");
        } else if (distance < Math.abs(c1r - c2r)) {
            System.out.println("0");
        } else if (distance == Math.abs(c1r - c2r)) {
            System.out.println("1");
        } else if (distance < c1r + c2r && distance > Math.abs(c1r - c2r)) {
            System.out.println("2");
        } else {
            System.out.println("3");
        }
    }
}
