#include <stdio.h>
#include <math.h>

typedef struct {
    double x, y;
} Point;

typedef struct {
    double cx, cy, r;
} Incircle;

Incircle incircle(Point p1, Point p2, Point p3) {
    double a = sqrt(pow(p2.x - p1.x, 2) + pow(p2.y - p1.y, 2));
    double b = sqrt(pow(p3.x - p2.x, 2) + pow(p3.y - p2.y, 2));
    double c = sqrt(pow(p1.x - p3.x, 2) + pow(p1.y - p3.y, 2));
    double s = (a + b + c) / 2;
    double area = sqrt(s * (s - a) * (s - b) * (s - c));
    double cx = ((a * p1.x) + (b * p2.x) + (c * p3.x)) / (2 * area);
    double cy = ((a * p1.y) + (b * p2.y) + (c * p3.y)) / (2 * area);
    double r = area / s;
    return (Incircle) {cx, cy, r};
}

int main() {
    Point p1, p2, p3;
    scanf("%lf %lf", &p1.x, &p1.y);
    scanf("%lf %lf", &p2.x, &p2.y);
    scanf("%lf %lf", &p3.x, &p3.y);
    Incircle inc = incircle(p1, p2, p3);
    printf("%.10f %.10f %.10f\n", inc.cx, inc.cy, inc.r);
    return 0;
}
