#include <iostream>
#include <cmath>

struct Point {
    double x, y;
};

double calculateArea(const Point& p1, const Point& p2, const Point& p3) {
    return abs((p1.x * (p2.y - p3.y) + p2.x * (p3.y - p1.y) + p3.x * (p1.y - p2.y)) / 2.0);
}

double calculateSemiPerimeter(const Point& p1, const Point& p2, const Point& p3) {
    double a = sqrt(pow(p2.x - p1.x, 2) + pow(p2.y - p1.y, 2));
    double b = sqrt(pow(p3.x - p2.x, 2) + pow(p3.y - p2.y, 2));
    double c = sqrt(pow(p1.x - p3.x, 2) + pow(p1.y - p3.y, 2));
    return (a + b + c) / 2.0;
}

Point calculateIncenter(const Point& p1, const Point& p2, const Point& p3) {
    double semiPerimeter = calculateSemiPerimeter(p1, p2, p3);
    double area = calculateArea(p1, p2, p3);
    double a = sqrt(pow(p2.x - p1.x, 2) + pow(p2.y - p1.y, 2));
    double b = sqrt(pow(p3.x - p2.x, 2) + pow(p3.y - p2.y, 2));
    double c = sqrt(pow(p1.x - p3.x, 2) + pow(p1.y - p3.y, 2));
    double x = (a * p1.x + b * p2.x + c * p3.x) / (a + b + c);
    double y = (a * p1.y + b * p2.y + c * p3.y) / (a + b + c);
    return {x, y};
}

double calculateRadius(const Point& p1, const Point& p2, const Point& p3) {
    double semiPerimeter = calculateSemiPerimeter(p1, p2, p3);
    double area = calculateArea(p1, p2, p3);
    return area / semiPerimeter;
}

int main() {
    Point p1, p2, p3;
    std::cin >> p1.x >> p1.y >> p2.x >> p2.y >> p3.x >> p3.y;
    Point incenter = calculateIncenter(p1, p2, p3);
    double radius = calculateRadius(p1, p2, p3);
    std::cout << incenter.x << " " << incenter.y << " " << radius << std::endl;
    return 0;
}
