#include <stdio.h>
#include <math.h>

typedef struct {
    double x, y;
} Point;

double distance(Point p1, Point p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
}

double area(Point p1, Point p2, Point p3) {
    return abs((p1.x * (p2.y - p3.y) + p2.x * (p3.y - p1.y) + p3.x * (p1.y - p2.y)) / 2.0);
}

double circumcircle(Point p1, Point p2, Point p3) {
    double a = distance(p1, p2);
    double b = distance(p2, p3);
    double c = distance(p3, p1);
    double s = (a + b + c) / 2.0;
    double area_triangle = sqrt(s * (s - a) * (s - b) * (s - c));
    double circumradius = (a * b * c) / (4 * area_triangle);
    return circumradius;
}

int main() {
    Point p1, p2, p3;
    scanf("%lf %lf", &p1.x, &p1.y);
    scanf("%lf %lf", &p2.x, &p2.y);
    scanf("%lf %lf", &p3.x, &p3.y);

    double ax = p2.y - p1.y;
    double ay = p1.x - p2.x;
    double bx = p3.y - p1.y;
    double by = p1.x - p3.x;
    double cx = p3.y - p2.y;
    double cy = p2.x - p3.x;

    double d = ax * by - ay * bx;
    double cx_circum = (ax * (p1.y * by - p2.y * by - p3.y * ay) + ay * (p2.x * by - p3.x * by - p1.x * ax) + by * (p3.x * ay - p1.x * ay - p2.x * bx)) / (2 * d);
    double cy_circum = (bx * (p1.y * by - p2.y * by - p3.y * ay) + by * (p2.x * by - p3.x * by - p1.x * bx) + ax * (p3.x * ay - p1.x * ay - p2.x * cx)) / (2 * d);
    double r = circumcircle(p1, p2, p3);

    printf("%.10lf %.10lf %.10lf\n", cx_circum, cy_circum, r);

    return 0;
}
