#include <iostream>
#include <cmath>

struct Point {
    double x, y;
};

double calculateDet(const Point& p1, const Point& p2, const Point& p3) {
    return (p1.x * (p2.y - p3.y) + p2.x * (p3.y - p1.y) + p3.x * (p1.y - p2.y));
}

double calculateArea(const Point& p1, const Point& p2, const Point& p3) {
    return std::abs(calculateDet(p1, p2, p3)) / 2;
}

double calculateRadius(const Point& p1, const Point& p2, const Point& p3) {
    double area = calculateArea(p1, p2, p3);
    double a = std::sqrt(std::pow(p2.x - p1.x, 2) + std::pow(p2.y - p1.y, 2));
    double b = std::sqrt(std::pow(p3.x - p2.x, 2) + std::pow(p3.y - p2.y, 2));
    double c = std::sqrt(std::pow(p1.x - p3.x, 2) + std::pow(p1.y - p3.y, 2));
    return (a * b * c) / (4 * area);
}

double calculateCx(const Point& p1, const Point& p2, const Point& p3) {
    return ((p1.x * p2.y + p2.x * p3.y + p3.x * p1.y) - (p2.x * p1.y + p3.x * p2.y + p1.x * p3.y)) / (2 * calculateDet(p1, p2, p3));
}

double calculateCy(const Point& p1, const Point& p2, const Point& p3) {
    return ((p1.y * p2.x + p2.y * p3.x + p3.y * p1.x) - (p2.y * p1.x + p3.y * p2.x + p1.y * p3.x)) / (2 * calculateDet(p1, p2, p3));
}

int main() {
    Point p1, p2, p3;
    std::cin >> p1.x >> p1.y >> p2.x >> p2.y >> p3.x >> p3.y;

    double cx = calculateCx(p1, p2, p3);
    double cy = calculateCy(p1, p2, p3);
    double r = calculateRadius(p1, p2, p3);

    std::cout << std::fixed << std::setprecision(15) << cx << " " << cy << " " << r << std::endl;

    return 0;
}
