#include <iostream>
#include <cmath>

using namespace std;

struct Point {
    double x, y;
};

struct Line {
    Point p1, p2;
};

double calculateDistance(Point p1, Point p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
}

double calculateIntersection(Point center, double radius, Line line) {
    double a = (line.p1.x - line.p2.x) / (line.p1.y - line.p2.y);
    double b = line.p1.x - a * line.p1.y;
    double x = (center.y - a * center.x - b) / (1 + pow(a, 2));
    double y = a * x + b;
    double distance = calculateDistance(center, {x, y});
    if (distance > radius) {
        return -1;
    }
    return x;
}

int main() {
    int cx, cy, r;
    cin >> cx >> cy >> r;
    int q;
    cin >> q;
    for (int i = 0; i < q; i++) {
        Line line;
        cin >> line.p1.x >> line.p1.y >> line.p2.x >> line.p2.y;
        double x1 = calculateIntersection({cx, cy}, r, line);
        double x2 = calculateIntersection({cx, cy}, r, {line.p2.x, line.p1.y});
        if (x1 == -1) {
            cout << line.p1.x << " " << line.p1.y << " " << line.p2.x << " " << line.p2.y << endl;
        } else if (x2 == -1) {
            cout << x1 << " " << calculateIntersection({cx, cy}, r, {line.p1.x, line.p2.y}) << " " << x1 << " " << calculateIntersection({cx, cy}, r, {line.p1.x, line.p2.y}) << endl;
        } else {
            cout << min(x1, x2) << " " << calculateIntersection({cx, cy}, r, {min(line.p1.x, line.p2.x), min(line.p1.y, line.p2.y)}) << " " << max(x1, x2) << " " << calculateIntersection({cx, cy}, r, {min(line.p1.x, line.p2.x), min(line.p1.y, line.p2.y)}) << endl;
        }
    }
    return 0;
}
