import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        double cx = scanner.nextDouble();
        double cy = scanner.nextDouble();
        double r = scanner.nextDouble();
        int q = scanner.nextInt();

        while (q-- > 0) {
            double x1 = scanner.nextDouble();
            double y1 = scanner.nextDouble();
            double x2 = scanner.nextDouble();
            double y2 = scanner.nextDouble();

            double a = x2 - x1;
            double b = y2 - y1;
            double c = x1 * y2 - x2 * y1;

            double D = a * a + b * b;
            double x = (a * (a * cx - b * cy) - b * c) / D;
            double y = (b * (a * cx - b * cy) + a * c) / D;

            double D1 = Math.sqrt((x - cx) * (x - cx) + (y - cy) * (y - cy)) - r;
            double D2 = Math.sqrt((x - cx) * (x - cx) + (y - cy) * (y - cy)) + r;

            if (Math.abs(D1) < 0.000001 && Math.abs(D2) < 0.000001) {
                System.out.printf("%.6f %.6f %.6f %.6f%n", x, y, x, y);
            } else if (Math.abs(D1) < 0.000001) {
                System.out.printf("%.6f %.6f %.6f %.6f%n", x, y, x2, y2);
            } else if (Math.abs(D2) < 0.000001) {
                System.out.printf("%.6f %.6f %.6f %.6f%n", x, y, x1, y1);
            } else {
                System.out.printf("%.6f %.6f %.6f %.6f%n", x1, y1, x2, y2);
            }
        }
    }
}
