import math

def get_cross_points(cx, cy, r, x1, y1, x2, y2):
    denominator = (x2 - x1) * (x2 - x1) + (y2 - y1) * (y2 - y1)
    if denominator == 0:
        return None

    t = ((x1 - cx) * (x2 - x1) + (y1 - cy) * (y2 - y1)) / denominator
    if t < 0 or t > 1:
        return None

    cross_x = x1 + t * (x2 - x1)
    cross_y = y1 + t * (y2 - y1)

    distance = math.sqrt((cross_x - cx) ** 2 + (cross_y - cy) ** 2)
    if distance > r:
        return None

    return (round(cross_x, 7), round(cross_y, 7))

def main():
    cx, cy, r = map(int, input().split())
    q = int(input())
    for _ in range(q):
        x1, y1, x2, y2 = map(int, input().split())
        cross_points = get_cross_points(cx, cy, r, x1, y1, x2, y2)
        if cross_points is not None:
            if cross_points[0] == cross_points[1]:
                print(f"{cross_points[0]} {cross_points[1]}")
            else:
                print(f"{cross_points[0]} {cross_points[1]}")

if __name__ == "__main__":
    main()
