#include <iostream>
#include <cmath>

int main() {
    double x1, y1, r1, x2, y2, r2;
    std::cin >> x1 >> y1 >> r1 >> x2 >> y2 >> r2;

    double d = sqrt(pow(x2 - x1, 2) + pow(y2 - y1, 2));
    double a = (pow(r1, 2) - pow(r2, 2) + pow(d, 2)) / (2 * d);
    double h = sqrt(pow(r1, 2) - pow(a, 2));
    double xm = x1 + a * (x2 - x1) / d;
    double ym = y1 + a * (y2 - y1) / d;
    double xs1 = xm + h * (y2 - y1) / d;
    double xs2 = xm - h * (y2 - y1) / d;
    double ys1 = ym - h * (x2 - x1) / d;
    double ys2 = ym + h * (x2 - x1) / d;

    if (std::abs(xs1 - xs2) < 0.000001) {
        std::cout << xs1 << " " << ys1 << " " << xs1 << " " << ys1 << std::endl;
    } else {
        std::cout << std::fixed << std::setprecision(10) << xs1 << " " << ys1 << " " << xs2 << " " << ys2 << std::endl;
    }

    return 0;
}
