import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        double x1 = scanner.nextDouble();
        double y1 = scanner.nextDouble();
        double r1 = scanner.nextDouble();
        double x2 = scanner.nextDouble();
        double y2 = scanner.nextDouble();
        double r2 = scanner.nextDouble();

        double d = Math.sqrt(Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2));
        double a = (Math.pow(r1, 2) - Math.pow(r2, 2) + Math.pow(d, 2)) / (2 * d);
        double h = Math.sqrt(Math.pow(r1, 2) - Math.pow(a, 2));
        double xm = x1 + a * (x2 - x1) / d;
        double ym = y1 + a * (y2 - y1) / d;
        double xs1 = xm + h * (y2 - y1) / d;
        double ys1 = ym - h * (x2 - x1) / d;
        double xs2 = xm - h * (y2 - y1) / d;
        double ys2 = ym + h * (x2 - x1) / d;

        System.out.printf("%.12f %.12f %.12f %.12f%n", xs1, ys1, xs2, ys2);
    }
}
