import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        double px = scanner.nextDouble();
        double py = scanner.nextDouble();
        double cx = scanner.nextDouble();
        double cy = scanner.nextDouble();
        double r = scanner.nextDouble();

        double dx = cx - px;
        double dy = cy - py;
        double d = Math.sqrt(dx * dx + dy * dy);
        double a = (r * r - d * d + dy * dy) / (2 * dy);
        double h = Math.sqrt(r * r - a * a);
        double ux = cx + a * dx / d;
        double uy = cy + a * dy / d;
        double vx = ux + h * dy / d;
        double vy = uy - h * dx / d;

        if (vx - cx < 0) {
            System.out.printf("%.10f %.10f%n", vx, vy);
            System.out.printf("%.10f %.10f%n", 2 * cx - vx, 2 * cy - vy);
        } else {
            System.out.printf("%.10f %.10f%n", 2 * cx - vx, 2 * cy - vy);
            System.out.printf("%.10f %.10f%n", vx, vy);
        }
    }
}
