#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_CHANNELS 100
#define MAX_COMMERCIALS 100
#define MAX_TIME 2400

int compare(const void *a, const void *b) {
    return (*(int*)a - *(int*)b);
}

int main() {
    int n, p, q;
    int channels[MAX_CHANNELS][MAX_COMMERCIALS][2];
    int commercialCount[MAX_CHANNELS];
    int maxDuration = 0;

    while (scanf("%d %d %d", &n, &p, &q) == 3) {
        if (n == 0 && p == 0 && q == 0) break;

        for (int i = 0; i < n; i++) {
            scanf("%d", &commercialCount[i]);
            for (int j = 0; j < commercialCount[i]; j++) {
                scanf("%d %d", &channels[i][j][0], &channels[i][j][1]);
            }
        }

        int durations[MAX_CHANNELS];
        for (int i = 0; i < n; i++) {
            durations[i] = q - p;
            for (int j = 0; j < commercialCount[i]; j++) {
                if (channels[i][j][0] < p) {
                    durations[i] -= (channels[i][j][1] - channels[i][j][0]);
                } else if (channels[i][j][1] > q) {
                    durations[i] -= (channels[i][j][1] - channels[i][j][0]);
                } else {
                    durations[i] -= (channels[i][j][1] - channels[i][j][0]);
                }
            }
        }

        int max = 0;
        for (int i = 0; i < n; i++) {
            max = (durations[i] > max) ? durations[i] : max;
        }

        int min = MAX_TIME;
        for (int i = 0; i < n; i++) {
            min = (channels[i][0][0] < min) ? channels[i][0][0] : min;
        }

        int maxDuration = 0;
        for (int i = 0; i < n; i++) {
            int start = (channels[i][0][0] < min) ? channels[i][0][0] : min;
            int end = (channels[i][0][1] > q) ? channels[i][0][1] : q;
            int duration = end - start;
            maxDuration = (duration > maxDuration) ? duration : maxDuration;
        }

        printf("%d\n", maxDuration);
    }

    return 0;
}
