#include <stdio.h>
#include <math.h>

#define PI 3.14159265359

typedef struct {
    double x, y, r;
} Circle;

void calculate_tangents(Circle c1, Circle c2) {
    double dx = c2.x - c1.x;
    double dy = c2.y - c1.y;
    double d = sqrt(dx * dx + dy * dy);
    double a = (c1.r * c1.r - c2.r * c2.r + d * d) / (2 * d);
    double h = sqrt(c1.r * c1.r - a * a);
    double xm = c1.x + a * dx / d;
    double ym = c1.y + a * dy / d;
    double xs1 = xm + h * dy / d;
    double xs2 = xm - h * dy / d;
    double ys1 = ym - h * dx / d;
    double ys2 = ym + h * dx / d;

    printf("%.10lf %.10lf\n", xs1, ys1);
    printf("%.10lf %.10lf\n", xs2, ys2);

    if (c1.x != c2.x) {
        double m = (c2.y - c1.y) / (c2.x - c1.x);
        double b = c1.y - m * c1.x;
        double x = (c1.r * c1.r - a * a) / (2 * h);
        double y = m * x + b;
        printf("%.10lf %.10lf\n", x, y);
        x = (c1.r * c1.r - a * a) / (2 * h);
        y = m * x + b;
        printf("%.10lf %.10lf\n", x, y);
    }
}

int main() {
    Circle c1, c2;
    scanf("%lf %lf %lf", &c1.x, &c1.y, &c1.r);
    scanf("%lf %lf %lf", &c2.x, &c2.y, &c2.r);
    calculate_tangents(c1, c2);
    return 0;
}
