#include <iostream>
#include <cmath>
#include <iomanip>

using namespace std;

struct Point {
    double x, y;
};

Point calculateTangentPoint(double c1x, double c1y, double c1r, double c2x, double c2y, double c2r) {
    double dx = c2x - c1x;
    double dy = c2y - c1y;
    double d = sqrt(dx * dx + dy * dy);
    double a = (c1r * c1r - c2r * c2r + d * d) / (2 * d);
    double h = sqrt(c1r * c1r - a * a);
    double xm = c1x + a * dx / d;
    double ym = c1y + a * dy / d;
    double xs1 = xm + h * dy / d;
    double xs2 = xm - h * dy / d;
    double ys1 = ym - h * dx / d;
    double ys2 = ym + h * dx / d;
    if (xs1 < xs2) {
        return {xs1, ys1};
    } else {
        return {xs2, ys2};
    }
}

int main() {
    double c1x, c1y, c1r, c2x, c2y, c2r;
    cin >> c1x >> c1y >> c1r;
    cin >> c2x >> c2y >> c2r;
    Point p1 = calculateTangentPoint(c1x, c1y, c1r, c2x, c2y, c2r);
    Point p2 = {c1x + (c2x - c1x) * (c1r - c2r) / d, c1y + (c2y - c1y) * (c1r - c2r) / d};
    if (p1.x < p2.x || (p1.x == p2.x && p1.y < p2.y)) {
        cout << fixed << setprecision(10) << p1.x << " " << p1.y << endl;
        cout << fixed << setprecision(10) << p2.x << " " << p2.y << endl;
        cout << fixed << setprecision(10) << (c2x - c1x) * (c1r - c2r) / d + c1x << " " << (c2y - c1y) * (c1r - c2r) / d + c1y << endl;
        cout << fixed << setprecision(10) << (c2x - c1x) * (c1r - c2r) / d + c1x << " " << (c2y - c1y) * (c1r - c2r) / d + c1y << endl;
    } else {
        cout << fixed << setprecision(10) << p2.x << " " << p2.y << endl;
        cout << fixed << setprecision(10) << p1.x << " " << p1.y << endl;
        cout << fixed << setprecision(10) << (c2x - c1x) * (c1r - c2r) / d + c1x << " " << (c2y - c1y) * (c1r - c2r) / d + c1y << endl;
        cout << fixed << setprecision(10) << (c2x - c1x) * (c1r - c2r) / d + c1x << " " << (c2y - c1y) * (c1r - c2r) / d + c1y << endl;
    }
    return 0;
}
