#include <stdio.h>
#include <math.h>

#define PI 3.14159265358979323846
#define EPS 1e-6

typedef struct {
    double x, y;
} Point;

typedef struct {
    Point p[100];
    int n;
} Polygon;

typedef struct {
    double x, y, r;
} Circle;

double distance(Point p1, Point p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
}

double area_of_triangle(Point p1, Point p2, Point p3) {
    return abs((p1.x * (p2.y - p3.y) + p2.x * (p3.y - p1.y) + p3.x * (p1.y - p2.y)) / 2.0);
}

double area_of_polygon(Polygon g) {
    double area = 0;
    for (int i = 0; i < g.n; i++) {
        area += area_of_triangle(g.p[i], g.p[(i + 1) % g.n], g.p[(i + 2) % g.n]);
    }
    return area;
}

double area_of_circle(Circle c) {
    return PI * pow(c.r, 2);
}

double area_of_intersection(Polygon g, Circle c) {
    double area = 0;
    for (int i = 0; i < g.n; i++) {
        Point p1 = g.p[i];
        Point p2 = g.p[(i + 1) % g.n];
        Point p3 = g.p[(i + 2) % g.n];
        double d1 = distance(p1, p2);
        double d2 = distance(p2, p3);
        double d3 = distance(p3, p1);
        double s = (d1 + d2 + d3) / 2.0;
        double A = sqrt(s * (s - d1) * (s - d2) * (s - d3));
        double h = (2 * A) / d1;
        double x = (p1.x + p2.x) / 2.0;
        double y = (p1.y + p2.y) / 2.0;
        double r = sqrt(pow(c.r, 2) - pow(h, 2));
        if (r > 0) {
            double theta = acos((pow(c.r, 2) - pow(h, 2)) / pow(c.r, 2));
            double area_triangle = 0.5 * A * h;
            double area_sector = 0.5 * theta * pow(c.r, 2);
            area += area_triangle + area_sector;
        }
    }
    return area;
}

int main() {
    int n;
    double r;
    scanf("%d %lf", &n, &r);
    Polygon g;
    g.n = n;
    for (int i = 0; i < n; i++) {
        scanf("%lf %lf", &g.p[i].x, &g.p[i].y);
    }
    Circle c;
    c.x = 0;
    c.y = 0;
    c.r = r;
    double area = area_of_polygon(g) - area_of_intersection(g, c);
    if (area < 0) {
        area = 0;
    }
    printf("%.5lf\n", area);
    return 0;
}
