#include <iostream>
#include <cmath>
#include <vector>

using namespace std;

double sign(double x) {
    return (x < 0) ? -1 : 1;
}

double area_of_intersection(int n, int r, vector<pair<int, int>> points) {
    double area = 0;
    for (int i = 0; i < n; i++) {
        int x1 = points[i].first;
        int y1 = points[i].second;
        int x2 = points[(i + 1) % n].first;
        int y2 = points[(i + 1) % n].second;
        double a = x1 * y2 - x2 * y1;
        double b = x2 - x1;
        double c = y2 - y1;
        double d = x1 * x1 + y1 * y1 - r * r;
        double e = (a * a) + (b * b) + (c * c);
        double f = (a * d) - (b * (x1 * c - y1 * b)) - (c * (x1 * b + y1 * a));
        double g = (f * f) - (e * d);
        if (g >= 0) {
            double h = sqrt(g);
            double intersection_x = (f - h) / e;
            double intersection_y = (x1 * c - y1 * b) / b + (intersection_x * b);
            if (intersection_x >= min(x1, x2) && intersection_x <= max(x1, x2) && intersection_y >= min(y1, y2) && intersection_y <= max(y1, y2)) {
                double distance = sqrt((x2 - x1) * (x2 - x1) + (y2 - y1) * (y2 - y1));
                double angle = acos((intersection_x - x1) / distance);
                double area_of_segment = 0.5 * distance * distance * sin(2 * angle);
                area += area_of_segment;
            }
        }
    }
    return area;
}

int main() {
    int n, r;
    cin >> n >> r;
    vector<pair<int, int>> points(n);
    for (int i = 0; i < n; i++) {
        cin >> points[i].first >> points[i].second;
    }
    cout << fixed << setprecision(8) << area_of_intersection(n, r, points) << endl;
    return 0;
}
