import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int r = scanner.nextInt();
        int[][] points = new int[n][2];
        for (int i = 0; i < n; i++) {
            points[i][0] = scanner.nextInt();
            points[i][1] = scanner.nextInt();
        }
        System.out.println(calculateIntersectionArea(n, r, points));
    }

    public static double calculateIntersectionArea(int n, int r, int[][] points) {
        double area = 0;
        for (int i = 0; i < n; i++) {
            int x1 = points[i][0];
            int y1 = points[i][1];
            int x2 = points[(i + 1) % n][0];
            int y2 = points[(i + 1) % n][1];
            double distance = Math.sqrt(Math.pow(x2 - x1, 2) + Math.pow(y2 - y1, 2));
            double angle = Math.acos((Math.pow(r, 2) + Math.pow(distance, 2) - Math.pow(r, 2)) / (2 * r * distance));
            double sectorArea = (angle * Math.pow(r, 2)) / 2;
            double triangleArea = (distance * r * Math.sin(angle)) / 2;
            area += sectorArea - triangleArea;
        }
        return area;
    }
}
