#include <stdio.h>
#include <math.h>

#define PI 3.14159265358979311600

double distance(double x1, double y1, double x2, double y2) {
    return sqrt(pow(x2 - x1, 2) + pow(y2 - y1, 2));
}

double area_of_intersection(double x1, double y1, double r1, double x2, double y2, double r2) {
    double d = distance(x1, y1, x2, y2);
    if (d > r1 + r2 || d <= abs(r1 - r2)) {
        return 0;
    }
    if (d == 0 && r1 == r2) {
        return PI * pow(r1, 2);
    }
    double a = (pow(r1, 2) * pow(r2, 2)) / pow(d, 2);
    double h = sqrt(pow(r1, 2) - pow(a, 2));
    double area = PI * h * (2 * a + h);
    return area;
}

int main() {
    double x1, y1, r1, x2, y2, r2;
    scanf("%lf %lf %lf %lf %lf %lf", &x1, &y1, &r1, &x2, &y2, &r2);
    printf("%.20lf\n", area_of_intersection(x1, y1, r1, x2, y2, r2));
    return 0;
}
