#include <iostream>
#include <cmath>

double areaOfIntersection(int c1x, int c1y, int c1r, int c2x, int c2y, int c2r) {
    double dx = c1x - c2x;
    double dy = c1y - c2y;
    double d = std::sqrt(dx * dx + dy * dy);
    double a = (c1r * c1r - c2r * c2r + d * d) / (2 * d);
    double h = std::sqrt(c1r * c1r - a * a);
    double area = 0.0;

    if (a == 0) {
        if (d == 0 && c1r == c2r) {
            area = std::pow(c1r, 2) * std::acos(-1);
        } else {
            return 0.0;
        }
    } else {
        area += 0.5 * std::acos((d + a - c1r) / (2 * a)) * h;
        area += 0.5 * std::acos((d + a - c2r) / (2 * a)) * h;
        area += 0.5 * std::acos((d - a - c1r) / (2 * h)) * a;
        area += 0.5 * std::acos((d - a - c2r) / (2 * h)) * a;
    }

    return area;
}

int main() {
    int c1x, c1y, c1r, c2x, c2y, c2r;
    std::cin >> c1x >> c1y >> c1r >> c2x >> c2y >> c2r;
    double result = areaOfIntersection(c1x, c1y, c1r, c2x, c2y, c2r);
    std::cout << std::fixed << std::setprecision(15) << result << std::endl;
    return 0;
}
