#include <iostream>
#include <vector>
#include <algorithm>
#include <limits>

using namespace std;

struct Time {
    int hour, minute;
};

bool operator<(const Time& a, const Time& b) {
    if (a.hour != b.hour) return a.hour < b.hour;
    return a.minute < b.minute;
}

int main() {
    int n, p, q;
    while (cin >> n >> p >> q) {
        if (n == 0) break;

        vector<Time> start(n), end(n);
        for (int i = 0; i < n; ++i) {
            int k;
            cin >> k;
            for (int j = 0; j < k; ++j) {
                int hour, minute;
                cin >> hour >> minute;
                start[i].hour = hour;
                start[i].minute = minute;
            }
        }

        for (int i = 0; i < n; ++i) {
            int k;
            cin >> k;
            for (int j = 0; j < k; ++j) {
                int hour, minute;
                cin >> hour >> minute;
                end[i].hour = hour;
                end[i].minute = minute;
            }
        }

        vector<Time> all;
        for (int i = 0; i < n; ++i) {
            all.push_back(start[i]);
            all.push_back(end[i]);
        }

        sort(all.begin(), all.end());

        int maxDuration = 0;
        int currentDuration = 0;
        int lastEnd = 0;
        for (const auto& time : all) {
            if (time < lastEnd) continue;
            if (time >= Time{p / 60, p % 60} && time <= Time{q / 60, q % 60}) {
                currentDuration = max(currentDuration, time.hour * 60 + time.minute - lastEnd.hour * 60 - lastEnd.minute);
            } else {
                currentDuration = 0;
            }
            lastEnd = time;
            maxDuration = max(maxDuration, currentDuration);
        }

        cout << maxDuration << endl;
    }

    return 0;
}
