import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        double c1x = scanner.nextDouble();
        double c1y = scanner.nextDouble();
        double c1r = scanner.nextDouble();
        double c2x = scanner.nextDouble();
        double c2y = scanner.nextDouble();
        double c2r = scanner.nextDouble();

        double d = Math.sqrt(Math.pow(c1x - c2x, 2) + Math.pow(c1y - c2y, 2));
        double a = (Math.pow(c1r, 2) * Math.log(1 + (Math.pow(d, 2) / (4 * Math.pow(c1r, 2) - Math.pow(d, 2)))));
        double b = (Math.pow(c2r, 2) * Math.log(1 + (Math.pow(d, 2) / (4 * Math.pow(c2r, 2) - Math.pow(d, 2)))));
        double c = (c1r * c2r * Math.sqrt(Math.pow(c1r + c2r, 2) - Math.pow(d, 2)));
        double area = a + b - c;

        if (d > c1r + c2r) {
            System.out.println(0);
        } else if (d < Math.abs(c1r - c2r)) {
            System.out.println(Math.PI * Math.pow(Math.min(c1r, c2r), 2));
        } else {
            System.out.printf("%.10f%n", area);
        }
    }
}
