import math

def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

def area_of_intersection(c1, c2):
    d = distance(c1, c2)
    if d > c1[2] + c2[2] or d < abs(c1[2] - c2[2]):
        return 0
    a = (c1[2]**2 * math.log(c1[2] + c2[2] - d) + c2[2]**2 * math.log(c1[2] + c2[2] + d) - c1[2]**2 * math.log(c1[2] - c2[2] - d) - c2[2]**2 * math.log(c1[2] - c2[2] + d)) / (2 * d)
    h = math.sqrt(c1[2]**2 - a**2)
    return a * math.acos((d**2 + a**2 - c2[2]**2) / (2 * d * a)) + h * math.acos((d**2 + h**2 - a**2) / (2 * d * h)) + h * math.acos((d**2 + h**2 - a**2) / (2 * d * h))

def main():
    c1 = list(map(int, input().split()))
    c2 = list(map(int, input().split()))
    print(format(area_of_intersection(c1, c2), '.10f'))

if __name__ == "__main__":
    main()
