#include <iostream>
#include <vector>
#include <algorithm>

int minCoins(int n, int m, std::vector<int>& coins) {
    std::vector<int> dp(n + 1, n + 1);
    dp[0] = 0;

    for (int i = 1; i <= n; i++) {
        for (int j = 0; j < m; j++) {
            if (coins[j] <= i) {
                dp[i] = std::min(dp[i], dp[i - coins[j]] + 1);
            }
        }
    }

    return dp[n] == n + 1 ? -1 : dp[n];
}

int main() {
    int n, m;
    std::cin >> n >> m;

    std::vector<int> coins(m);
    for (int i = 0; i < m; i++) {
        std::cin >> coins[i];
    }

    std::sort(coins.begin(), coins.end());

    int result = minCoins(n, m, coins);
    if (result != -1) {
        std::cout << result << std::endl;
    } else {
        std::cout << "Not possible to make change" << std::endl;
    }

    return 0;
}
