#include <iostream>
#include <vector>
#include <algorithm>

int knapsack(int n, int w, std::vector<int>& v, std::vector<int>& w) {
    std::vector<std::vector<int>> dp(n + 1, std::vector<int>(w + 1, 0));

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= w; j++) {
            if (w[i - 1] <= j) {
                dp[i][j] = std::max(v[i - 1] + dp[i - 1][j - w[i - 1]], dp[i - 1][j]);
            } else {
                dp[i][j] = dp[i - 1][j];
            }
        }
    }

    return dp[n][w];
}

int main() {
    int n, w;
    std::cin >> n >> w;

    std::vector<int> v(n), w(n);
    for (int i = 0; i < n; i++) {
        std::cin >> v[i] >> w[i];
    }

    std::cout << knapsack(n, w, v, w) << std::endl;

    return 0;
}
