#include <stdio.h>
#include <string.h>

int min(int a, int b, int c) {
    return (a < b) ? (a < c ? a : c) : (b < c ? b : c);
}

int edit_distance(char *s1, char *s2) {
    int m = strlen(s1);
    int n = strlen(s2);
    int d[m + 1][n + 1];

    for (int i = 0; i <= m; i++) {
        for (int j = 0; j <= n; j++) {
            if (i == 0)
                d[i][j] = j;
            else if (j == 0)
                d[i][j] = i;
            else if (s1[i - 1] == s2[j - 1])
                d[i][j] = d[i - 1][j - 1];
            else
                d[i][j] = 1 + min(d[i - 1][j], d[i][j - 1], d[i - 1][j - 1]);
        }
    }

    return d[m][n];
}

int main() {
    char s1[1001], s2[1001];

    scanf("%s", s1);
    scanf("%s", s2);

    printf("%d\n", edit_distance(s1, s2));

    return 0;
}
