import sys

def read_input():
    n, p, q = map(int, input().split())
    p, q = int(p / 60), int(q / 60)
    intervals = []
    for _ in range(n):
        k = int(input())
        interval = []
        for _ in range(k):
            start, end = map(int, input().split())
            interval.append((start // 60, end // 60))
        intervals.append(interval)
    return n, p, q, intervals

def find_max_duration(n, p, q, intervals):
    max_duration = 0
    for i in range(n):
        for j in range(n):
            if i == j:
                continue
            max_start, max_end = 0, 0
            for k in range(p, q + 1):
                if k < intervals[i][0][0]:
                    max_start = max(max_start, k)
                elif k >= intervals[i][0][1]:
                    max_end = max(max_end, k)
                elif k >= intervals[i][1][0]:
                    max_start = max(max_start, k)
                elif k >= intervals[i][1][1]:
                    max_end = max(max_end, k)
            if max_start == 0 or max_end == 0:
                continue
            for k in range(p, q + 1):
                if k < intervals[j][0][0]:
                    max_start = max(max_start, k)
                elif k >= intervals[j][0][1]:
                    max_end = max(max_end, k)
                elif k >= intervals[j][1][0]:
                    max_start = max(max_start, k)
                elif k >= intervals[j][1][1]:
                    max_end = max(max_end, k)
            if max_start == 0 or max_end == 0:
                continue
            duration = (max_end - max_start) * 60
            max_duration = max(max_duration, duration)
    return max_duration

def main():
    while True:
        n, p, q, intervals = read_input()
        if n == 0:
            break
        max_duration = find_max_duration(n, p, q, intervals)
        print(max_duration)

if __name__ == "__main__":
    main()
