#include <iostream>
#include <vector>
#include <algorithm>

int knapsack(int n, int w, std::vector<std::pair<int, int>>& items) {
    std::vector<std::vector<int>> dp(n + 1, std::vector<int>(w + 1, 0));

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= w; j++) {
            if (items[i - 1].second > j) {
                dp[i][j] = dp[i - 1][j];
            } else {
                dp[i][j] = std::max(dp[i - 1][j], dp[i - 1][j - items[i - 1].second] + items[i - 1].first);
            }
        }
    }

    return dp[n][w];
}

int main() {
    int n, w;
    std::cin >> n >> w;

    std::vector<std::pair<int, int>> items(n);
    for (int i = 0; i < n; i++) {
        std::cin >> items[i].first >> items[i].second;
    }

    std::sort(items.begin(), items.end(), [](const auto& a, const auto& b) {
        return a.second < b.second;
    });

    std::cout << knapsack(n, w, items) << std::endl;

    return 0;
}
