#include <stdio.h>
#include <stdlib.h>

#define N 100
#define W 10000
#define INF 1000000000

int knapsack(int n, int w, int v[], int w[], int m[]) {
    int dp[n + 1][w + 1][n + 1];

    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= w; j++) {
            for (int k = 0; k <= n; k++) {
                dp[i][j][k] = -INF;
            }
        }
    }

    dp[0][0][0] = 0;

    for (int i = 1; i <= n; i++) {
        for (int j = 0; j <= w; j++) {
            for (int k = 0; k <= m[i - 1]; k++) {
                if (j >= w[i - 1]) {
                    dp[i][j][k] = dp[i - 1][j][k];
                    if (k > 0 && dp[i - 1][j - w[i - 1]][k - 1] + v[i - 1] > dp[i][j][k]) {
                        dp[i][j][k] = dp[i - 1][j - w[i - 1]][k - 1] + v[i - 1];
                    }
                } else {
                    dp[i][j][k] = dp[i - 1][j][k];
                }
            }
        }
    }

    int max_val = -INF;
    for (int k = 0; k <= n; k++) {
        if (dp[n][w][k] > max_val) {
            max_val = dp[n][w][k];
        }
    }

    return max_val;
}

int main() {
    int n, w;
    scanf("%d %d", &n, &w);

    int v[n], w[n], m[n];
    for (int i = 0; i < n; i++) {
        scanf("%d %d %d", &v[i], &w[i], &m[i]);
    }

    int max_val = knapsack(n, w, v, w, m);
    printf("%d\n", max_val);

    return 0;
}
