#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int MAX_N = 100;
const int MAX_W = 10000;

int knapsack(int n, int w, vector<pair<int, pair<int, int>>> items) {
    vector<vector<vector<int>>> dp(n + 1, vector<vector<int>>(w + 1, vector<int>(MAX_N + 1, 0)));

    for (int i = 1; i <= n; i++) {
        for (int j = 0; j <= w; j++) {
            for (int k = 0; k <= items[i - 1].second.first; k++) {
                if (j == 0 || k == 0) {
                    dp[i][j][k] = 0;
                } else if (items[i - 1].first <= j) {
                    dp[i][j][k] = max(dp[i - 1][j][k], dp[i - 1][j - items[i - 1].first][k - 1] + items[i - 1].first);
                } else {
                    dp[i][j][k] = dp[i - 1][j][k];
                }
            }
        }
    }

    return dp[n][w][items[n].second.first];
}

int main() {
    int n, w;
    cin >> n >> w;

    vector<pair<int, pair<int, int>>> items(n);
    for (int i = 0; i < n; i++) {
        cin >> items[i].first >> items[i].second.first >> items[i].second.second;
    }

    sort(items.begin(), items.end(), [](const pair<int, pair<int, int>>& a, const pair<int, pair<int, int>>& b) {
        return a.second.first / a.second.second > b.second.first / b.second.second;
    });

    cout << knapsack(n, w, items) << endl;

    return 0;
}
