#include <stdio.h>

int main() {
    int heights[10];
    int i, j, temp;

    // Read heights of mountains
    for (i = 0; i < 10; i++) {
        printf("Enter height of mountain %d: ", i + 1);
        scanf("%d", &heights[i]);
    }

    // Sort heights in descending order
    for (i = 0; i < 10; i++) {
        for (j = i + 1; j < 10; j++) {
            if (heights[i] < heights[j]) {
                temp = heights[i];
                heights[i] = heights[j];
                heights[j] = temp;
            }
        }
    }

    // Print heights of top 3 mountains
    printf("Height of the 1st mountain: %d\n", heights[0]);
    printf("Height of the 2nd mountain: %d\n", heights[1]);
    printf("Height of the 3rd mountain: %d\n", heights[2]);

    return 0;
}
