#include <stdio.h>
#include <math.h>

typedef struct {
    double x, y;
} Point;

double distance(Point p1, Point p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
}

double circumcenter(Point p1, Point p2, Point p3) {
    double a = distance(p1, p2);
    double b = distance(p2, p3);
    double c = distance(p3, p1);
    double s = (a + b + c) / 2;
    double area = sqrt(s * (s - a) * (s - b) * (s - c));
    double x = ((s - b) * (s - c)) / (2 * area);
    double y = ((s - a) * (s - c)) / (2 * area);
    return sqrt(pow(x - p1.x, 2) + pow(y - p1.y, 2));
}

int main() {
    int n;
    scanf("%d", &n);
    while (n--) {
        Point p1, p2, p3;
        scanf("%lf %lf %lf %lf %lf %lf", &p1.x, &p1.y, &p2.x, &p2.y, &p3.x, &p3.y);
        double r = circumcenter(p1, p2, p3);
        printf("%.3lf %.3lf %.3lf\n", (p1.x + p2.x + p3.x) / 3, (p1.y + p2.y + p3.y) / 3, r);
    }
    return 0;
}
