#include <stdio.h>
#include <stdlib.h>

#define MAX_N 41
#define MAX_W 1016

int knapsack(int n, int w, int v[], int w[], int dp[][MAX_W]) {
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= w; j++) {
            if (i == 0 || j == 0) {
                dp[i][j] = 0;
            } else if (w[i - 1] <= j) {
                dp[i][j] = (v[i - 1] + dp[i - 1][j - w[i - 1]]) > dp[i - 1][j] ? 
                           (v[i - 1] + dp[i - 1][j - w[i - 1]]) : dp[i - 1][j];
            } else {
                dp[i][j] = dp[i - 1][j];
            }
        }
    }
    return dp[n][w];
}

int main() {
    int n, w;
    scanf("%d %d", &n, &w);

    int v[MAX_N], w[MAX_N];
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &v[i], &w[i]);
    }

    int dp[MAX_N][MAX_W];
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= w; j++) {
            dp[i][j] = 0;
        }
    }

    printf("%d\n", knapsack(n, w, v, w, dp));

    return 0;
}
