#include <iostream>
#include <vector>

void printMatrix(const std::vector<std::vector<int>>& matrix) {
    for (const auto& row : matrix) {
        for (int val : row) {
            printf("%3d", val);
        }
        printf("\n");
    }
}

void zigzag(int n) {
    std::vector<std::vector<int>> matrix(n, std::vector<int>(n, 0));
    int dir = 1;
    int x = 0, y = 0;
    int count = 1;

    while (x >= 0 && x < n && y >= 0 && y < n) {
        matrix[x][y] = count++;
        if (dir == 1) {
            if (y + 1 < n && matrix[x][y + 1] == 0) {
                y++;
            } else if (x + 1 < n && matrix[x + 1][y] == 0) {
                x++;
                dir = 2;
            } else {
                y--;
                dir = 3;
            }
        } else if (dir == 2) {
            if (x + 1 < n && matrix[x + 1][y] == 0) {
                x++;
            } else if (y - 1 >= 0 && matrix[x][y - 1] == 0) {
                y--;
                dir = 1;
            } else {
                x--;
                dir = 4;
            }
        } else if (dir == 3) {
            if (y - 1 >= 0 && matrix[x][y - 1] == 0) {
                y--;
            } else if (x - 1 >= 0 && matrix[x - 1][y] == 0) {
                x--;
                dir = 2;
            } else {
                y++;
                dir = 1;
            }
        } else {
            if (x - 1 >= 0 && matrix[x - 1][y] == 0) {
                x--;
            } else if (y + 1 < n && matrix[x][y + 1] == 0) {
                y++;
                dir = 3;
            } else {
                x++;
                dir = 1;
            }
        }
    }

    printf("Case %d:\n", n);
    printMatrix(matrix);
}

int main() {
    int n;
    int caseNum = 1;
    while (std::cin >> n && n != 0) {
        zigzag(n);
        caseNum++;
    }
    return 0;
}
