import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int caseNum = 1;
        while (true) {
            int n = scanner.nextInt();
            if (n == 0) break;
            System.out.println("Case " + caseNum + ":");
            int[][] matrix = new int[n][n];
            int count = 1;
            int row = 0, col = 0;
            while (row < n && col < n) {
                if (row < col) {
                    for (int i = col; i >= row; i--) {
                        matrix[row][i] = count++;
                    }
                    row++;
                } else if (row > col) {
                    for (int i = row; i < n; i++) {
                        matrix[i][col] = count++;
                    }
                    col++;
                } else {
                    matrix[row][col] = count++;
                    row++;
                    col++;
                }
            }
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    System.out.printf("%3d", matrix[i][j]);
                }
                System.out.println();
            }
            System.out.println();
            caseNum++;
        }
    }
}
